import { useEffect, useState } from "react";
import axios from 'axios';
const ParentReducer = () => {


const [initialState, setState] = useState([])
useEffect(() => {
    // API Users
    axios.get('http://localhost:4000/students')
    .then((res) => {
        setState(res.data);
    });
})

// const initialState = {infoContacts: [
//     {id: 1, nom: "Ahmed", email: "Ahmed@gmail.com"},
//     {id: 2, nom: "Ahlam", email: "Ahlam@gmail.com"},
//     {id: 3, nom: "Said", email: "Said@gmail.com"}
// ]}
const reducer = (state = initialState, action) => {
    switch(action.type) {
        case "AddContact":
            return {...state,initialState: [...state.initialState,action.payload] };
            
            
        case "EditeContact":
            const contact = state.initialState.find((cn) => cn.id == action.payload.id );
            if(contact) {
                contact.nom=  action.payload.nom ;
                contact.email=  action.payload.email ;
            }
            return state;
            
        case "DetailContact":
            return {};
            
        case "DeleteContact":
            return {...state,initialState: [...state.initialState.filter(n=>n.id !== parseInt(action.payload))]};
            
        default: 
            return state
    }

}
}
export default ParentReducer;
