// ! ----------------------import----------------------
import { useState } from "react";
import { Link, useNavigate    } from "react-router-dom";
import { AddContact } from "./actionC.js";
import { useSelector, useDispatch } from "react-redux";

// ! ----------------------Component----------------------
const AjouterContact = () => {
    // * ---------------------Consts ---------------------
    const [inputnom, setNom] = useState('');
    const [inputemail, setEmail] = useState('');
    const contacts = useSelector(data => data.infoContacts);
    const dispatch = useDispatch();
    const history = useNavigate(); 
    // * ---------------------Handle ( Nom $ Email) ---------------------
    const handleNom = (e) => {
        setNom(e.target.value);
    };
    const handleEmail = (e) => {
        setEmail(e.target.value);
    };

    // * ---------------------Handle Submit ------------------------------
    const handleSubmit = (e) => {
        
        // e.preventDefault();
        const donner = {id: contacts.length+1, nom: inputnom, email: inputemail };
        dispatch(AddContact(donner));
        history('/')
    };
    // * --------------------------return ------------------------------
    return(
        <div>
            <h1>Ajouter de Contact </h1><hr></hr>
                <form  onSubmit={handleSubmit}> 
                    Nom: <input type="text" value={inputnom} onChange={handleNom} required/> 
                    Email: <input type="text" value={inputemail} onChange={handleEmail} required/> 
                    {/* ---------------- ---Ajouter ------------------- */}
                    
                    <input 
                            type="submit"
                            style={{marginLeft: "5px"}} 
                            className="btn btn-success" 
                            value="Ajouter Contact"
                            // onClick={handleClickAjt}
                            
                    />  
                </form>
        </div>
    
    )}
// ! ----------------------export----------------------
export default AjouterContact;
