import axios from 'axios';

const fetchData = async () => {
    try {
        const response = await axios.get('http://localhost:3001/students');
        // console.log(response.data);
        return response.data;
    } catch (error) {
        console.error('Error fetching data:', error);
        return []; // Return an empty array or handle the error as needed
    }
};

const initialState = {
    infoContacts: [] // Initialize with an empty array
};

// Fetch data asynchronously in the initialization of the reducer
fetchData()
.then(data => {
    initialState.infoContacts = data;
    console.log(initialState.infoContacts)

})
.catch(error => {
    console.error('Error setting initial state:', error);
});

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "AddContact":
            axios.post("http://localhost:3001/students/create", action.payload)
            .then(response => {
                // Handle success if needed
                console.log(response.data);
            })
            .catch(error => {
                // Handle error if needed
                console.error('Error adding contact:', error);
            });


        case "EditeContact":
            const contact = state.infoContacts.find((cn) => cn.id == action.payload.id);
            if (contact) {
                contact.nom = action.payload.nom;
                contact.email = action.payload.email;
            }
            return state;

        case "DetailContact":
            return {};

        case "DeleteContact":
            return { ...state, infoContacts: [...state.infoContacts.filter(n => n.id !== parseInt(action.payload))] };

        default:
            return state
    }

}

export default reducer;
