// const initialState = {infoContacts: async refStudent(){
// axios
// } }
const [state, setState] = useState({ infoContacts : [] })
// const initialState = {infoContacts: [
//     {id: 1, nom: "Ahmed", email: "Ahmed@gmail.com"},
//     {id: 2, nom: "Ahlam", email: "Ahlam@gmail.com"},
//     {id: 3, nom: "Said", email: "Said@gmail.com"}
// ]}
const reducer = (state = initialState, action) => {
    switch(action.type) {
        case "AddContact":
            return {...state,infoContacts: [...state.infoContacts,action.payload] };
            
            
        case "EditeContact":
            const contact = state.infoContacts.find((cn) => cn.id == action.payload.id );
            if(contact) {
                contact.nom=  action.payload.nom ;
                contact.email=  action.payload.email ;
            }
            return state;
            
        case "DetailContact":
            return {};
            
        case "DeleteContact":
            return {...state,infoContacts: [...state.infoContacts.filter(n=>n.id !== parseInt(action.payload))]};
            
        default: 
            return state
    }

}
export default reducer;
