import axios from 'axios'
const data = async () => {
    try {
        const response = await axios.get('http://localhost:3001/students/');
        return response.data;
        // console.log(response.data); // You can log or process the data as needed
    } catch (error) {
        console.error('Error fetching data:', error);
    }
};
const initialState = {infoContacts: [
    {id: 1, nom: "Ahmed", email: "Ahmed@gmail.com"},
    {id: 2, nom: "Ahlam", email: "Ahlam@gmail.com"},
    {id: 3, nom: "Said", email: "Said@gmail.com"}
]}
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "AddContact":
            axios.post("http://localhost:3001/students/create", action.payload)
            .then(response => {
                // Handle success if needed
                console.log(response.data);
            })
            .catch(error => {
                // Handle error if needed
                console.error('Error adding contact:', error);
            });


        case "EditeContact":
            const contact = state.infoContacts.find((cn) => cn.id == action.payload.id);
            if (contact) {
                contact.nom = action.payload.nom;
                contact.email = action.payload.email;
            }
            return state;

        case "DetailContact":
            return {};

        case "DeleteContact":
            return { ...state, infoContacts: [...state.infoContacts.filter(n => n.id !== parseInt(action.payload))] };

        default:
            return state
    }

}

export default reducer;
