import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom"
import { EditeContact } from "./actionC.js";

const UpdateContact = () => {
    const age = useParams().age;
    const contact = useSelector(data=>data.infoContacts.find((user) => user.age == age));
    const [name, setName] = useState(contact ? contact.nom : '');
    const [prenom, set] = useState(contact ? contact.prenom : '');
    const history = useNavigate();
    const  dispatch = useDispatch();
    if (!contact) {
        return <div><h1>Contact not found</h1></div>;
    }
    
    const  submitHandler= (e)=>{
        const newContact = {prenom: inputprenom, nom: inputnom,  age};
        dispatch(EditeContact(newContact));
        history('/');
    }
    return (
        <div className="container" style={{backgroundColor: '#f4f5f7'}}>
            <h1>Ajouter de Contact </h1><hr></hr>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label for="prenom">Prenom :</label>
                    <input type="text" className="form-control" id="prenom" placeholder="Enter Name" name="prenom" value={inputprenom} onChange={handlePrenom} required/>
                </div>
                <div className="form-group">
                    <label for="name">Nom :</label>
                    <input type="text" className="form-control" id="name" placeholder="Enter Name" name="nom" value={inputnom} onChange={handleNom} required/>
                </div>
                <button type="submit" className="btn btn-success my-2">Envoyer</button>
            </form>
        </div>
    
    )
    
}
export default  UpdateContact;
